import Dropzone, { useDropzone } from 'react-dropzone'

import { DropContainer, UploadMessage } from './styles'

function Upload() {
	const {
		acceptedFiles,
		getRootProps,
		getInputProps,
		isDragActive,
		isDragReject,
	} = useDropzone({
		accept: 'image/*',
	})

	const renderDragMessage = (dragActive, dragReject) => {
		if (!dragActive)
			return (
				<UploadMessage>
					Arraste os arquivos aqui...
				</UploadMessage>
            )
        else if (!dragReject)
            return (
                <UploadMessage>
                    A
                </UploadMessage>
            )
	}

	return (
		<Dropzone onDropAccepted={() => {}}>
			{() => (
				<DropContainer
					{...getRootProps()}
					isDragActive={isDragActive}
					isDragReject={isDragReject}>
					<input {...getInputProps()} />
					{renderDragMessage(
						isDragActive,
						isDragReject
					)}
				</DropContainer>
			)}
		</Dropzone>
	)
}

export default Upload
